package com.example.imagesnestedurl;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;

public class Zorbing extends AppCompatActivity {
    TextView tv1, tv2;
    ImageView iv;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_zorbing);
        tv1 = findViewById(R.id.tv1);
        tv2 = findViewById(R.id.tv2);
        iv = findViewById(R.id.iv);
        Bundle b = this.getIntent().getExtras();

        if (b != null) {
            tv1.setText(b.getString("str_val", null));
            Glide.with(Zorbing.this).load("data:image/jpeg;base64,").into(iv);

            String str = "Zorbing (also known as globe-riding, sphereing, orbing) is the recreation or sport of rolling downhill inside an orb, typically made of transparent plastic.Zorbing is generally performed on a gentle slope but can also be done on a level surface, permitting more rider control. ";
            tv2.setText(str);
        }
    }
}